import pickleimport pandas as pdimport matplotlib.pyplot as pltimport numpy as npimport seaborn as sns# Load datadf = pd.read_csv("diabetes.csv")X=df.iloc[:,:-1]y=df.iloc[:,-1]df.isnull().sum()from sklearn.model_selection import train_test_splitX_train, X_test,y_train,y_test=train_test_split(X,y,test_size=0.2)from sklearn.linear_model import LogisticRegressionregression_model=LogisticRegression()regression_model.fit(X_train,y_train)from sklearn.metrics import accuracy_scorey_pred=regression_model.predict(X_test)score=accuracy_score(y_pred,y_test)print(score)print(y_pred)print(regression_model.predict(np.array([[8,100,74,40,215,39.4,0.661,43]])))pickle.dump(regression_model, open("model.pkl","wb"))